require 'rspec'
require 'big_number'

RSpec.describe String do

  it 'a > b, number a has more digits' do
    a = ''
    b = ''

    expect(a <=> b).to eql 1
  end

  it 'a > b, a - b = 1' do
    a = ''
    b = ''

    expect(a <=> b).to eql 1
  end

  it 'b < a, a - b = 1' do
    a = ''
    b = ''

    expect(b <=> a).to eql -1
  end

  it 'a = b' do
    a = ''
    b = ''

    expect(b <=> a).to eql 0
  end

  it 'a > b, b is empty' do
    a = ''
    b = ''

    expect(b <=> a).to eql -1
  end

  it 'a > b, b is 0' do
    a = ''
    b = '0'

    expect(a <=> b).to eql 1
  end

  it 'a = b' do
    a = '0'
    b = '0'

    expect(a <=> b).to eql 0
  end
end